public class MyForm : Form, INotifyPropertyChanged\n{\n    private bool isDirty;\n\n    public event PropertyChangedEventHandler PropertyChanged;\n\n    public MyForm()\n    {\n        InitializeComponent();\n        // Initialize databinding here...\n    }\n\n    // Assume you have a TextBox control named textBox1\n    private void textBox1_TextChanged(object sender, EventArgs e)\n    {\n        if (!isDirty && !textBox1.DataBindings[0].IsBinding)\n        {\n            isDirty = true;\n            OnPropertyChanged(new PropertyChangedEventArgs("IsDirty"));\n        }\n    }\n\n    public bool IsDirty\n    {\n        get { return isDirty; }\n        private set { isDirty = value; OnPropertyChanged(new PropertyChangedEventArgs("IsDirty")); }\n    }\n\n    protected virtual void OnPropertyChanged(PropertyChangedEventArgs e)\n    {\n        PropertyChanged?.Invoke(this, e);\n    }\n}