using System.Collections.Generic;\nusing System.Configuration;\n\npublic class RecipientSettings : ApplicationSettingsBase\n{\n    [UserScopedSetting()]\n    [SettingsSerializeAs(SettingsSerializeAs.Xml)]\n    public Dictionary<string, string> Recipients\n    {\n        get { return (Dictionary<string, string>)this["Recipients"]; }\n        set { this["Recipients"] = value; }\n    }\n}