using System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\n\npublic class ImageGenerator : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Create a new bitmap\n        Bitmap bitmap = new Bitmap(400, 200);\n\n        // Create a graphics object from the bitmap\n        Graphics graphics = Graphics.FromImage(bitmap);\n\n        // Clear the graphics object with a white background\n        graphics.Clear(Color.White);\n\n        // Draw a simple chart\n        graphics.DrawLine(Pens.Black, 10, 10, 390, 190);\n\n        // Save the bitmap to the output stream\n        bitmap.Save(context.Response.OutputStream, ImageFormat.Png);\n\n        // Dispose of the graphics object and bitmap\n        graphics.Dispose();\n        bitmap.Dispose();\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}