// Embed native DLLs as resources in your .NET wrapper DLL\n// ...\n\n// Extract and load native DLLs at runtime\n[DllImport("kernel32", CharSet = CharSet.Unicode, SetLastError = true)]\nprivate static extern IntPtr LoadLibrary(string lpFileName);\n\nprivate void LoadNativeDlls()\n{\n    string[] dlls = { "cmr.dll", "pnl.dll", "opencv.dll" }; // list of native DLLs\n\n    foreach (string dll in dlls)\n    {\n        string tempPath = Path.Combine(Path.GetTempPath(), dll);\n        using (Stream stream = Assembly.GetExecutingAssembly().GetManifestResourceStream(dll))\n        {\n            using (FileStream fs = new FileStream(tempPath, FileMode.Create))\n            {\n                stream.CopyTo(fs);\n            }\n        }\n\n        IntPtr hModule = LoadLibrary(tempPath);\n        if (hModule == IntPtr.Zero)\n        {\n            // Handle error\n        }\n    }\n}