using Castle.Windsor;\nusing Castle.Windsor.Configuration;\nusing NUnit.Framework;\n\n[TestFixture]\npublic class ContainerTests\n{\n    [Test]\n    public void AllRegisteredComponentsCanBeResolved()\n    {\n        // Create a new Windsor container\n        var container = new WindsorContainer();\n\n        // Load the container configuration\n        container.Install(Configuration.FromAppConfig());\n\n        // Get all registered components\n        var components = container.GetComponents();\n\n        // Try to resolve each component\n        foreach (var component in components)\n        {\n            try\n            {\n                container.Resolve(component.Key);\n            }\n            catch (Exception ex)\n            {\n                Assert.Fail($"Component {component.Key} cannot be resolved: {ex.Message}");\n            }\n        }\n    }\n}