using CabLib;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create a new cabinet file\n        using (var cab = new CabFile("example.cab"))\n        {\n            // Add files to the cabinet\n            cab.AddFile("file1.txt", "file1.txt");\n            cab.AddFile("file2.txt", "file2.txt");\n\n            // Save the cabinet file\n            cab.Save();\n        }\n    }\n}