using Perforce.P4;\n\nclass PerforceExample\n{\n    static void Main(string[] args)\n    {\n        // Set up the Perforce connection\n        P4Connection connection = new P4Connection();\n        connection.Port = "perforce:1666";  // replace with your Perforce server and port\n        connection.User = "username";       // replace with your Perforce username\n        connection.Password = "password";   // replace with your Perforce password\n        connection.Client = "clientname";   // replace with your Perforce client name\n\n        // Connect to the Perforce server\n        connection.Connect();\n\n        try\n        {\n            // Run a Perforce command, e.g. "p4 files"\n            P4Command command = new P4Command(connection, "files", new string[] { "//..." });\n            P4CommandResult result = command.Run();\n\n            // Process the results\n            foreach (P4File file in result.Files)\n            {\n                Console.WriteLine(file.DepotPath);\n            }\n        }\n        finally\n        {\n            // Disconnect from the Perforce server\n            connection.Disconnect();\n        }\n    }\n}