// In your web.config file\n<system.web>\n  <authentication mode="Forms" />\n  <authorization>\n    <allow users="*" />\n  </authorization>\n</system.web>\n\n// In your code-behind file\nprotected void Page_Load(object sender, EventArgs e)\n{\n  string folder = Request.Url.AbsolutePath;\n  if (folder.StartsWith("/folder1"))\n  {\n    // Check if the user is authenticated using Windows auth\n    if (!HttpContext.Current.Request.LogonUserIdentity.IsAuthenticated)\n    {\n      // Redirect to a login page or return an error\n    }\n  }\n  else if (folder.StartsWith("/folder2"))\n  {\n    // Check if the user is authenticated using Forms auth\n    if (!HttpContext.Current.User.Identity.IsAuthenticated)\n    {\n      // Redirect to a login page or return an error\n    }\n  }\n}