using System.Text.RegularExpressions;\n\npublic static string EnsureWordLength(this string target, int length)\n{\n    // Remove HTML tags\n    var plainText = Regex.Replace(target, "<.*?>", "");\n\n    // Split the string into words of a specified length\n    var words = new List<string>();\n    for (int i = 0; i < plainText.Length; i += length)\n    {\n        words.Add(plainText.Substring(i, Math.Min(length, plainText.Length - i)));\n    }\n\n    return string.Join(" ", words);\n}