public abstract class FooBase\n{\n    public event EventHandler<StatusTextChangedEventArgs> StatusTextChanged;\n    private string _statusText = "Idle.";\n    public string StatusText\n    {\n        get { return _statusText; }\n        protected set\n        {\n            _statusText = value;\n            StatusTextChanged?.Invoke(this, new StatusTextChangedEventArgs(value));\n        }\n    }\n}\n\npublic class StatusTextChangedEventArgs : EventArgs\n{\n    public string StatusText { get; set; }\n    public StatusTextChangedEventArgs(string statusText)\n    {\n        StatusText = statusText;\n    }\n}