public class MyComponent : Component\n{\n    private Form GetParentForm()\n    {\n        IContainer container = Container;\n        if (container != null)\n        {\n            foreach (IComponent component in container.Components)\n            {\n                if (component is Form)\n                {\n                    return (Form)component;\n                }\n            }\n        }\n        return null;\n    }\n}