// Test class for modify method\n[TestClass]\npublic class ModifyTests\n{\n    [TestMethod]\n    public void Modify_ValueLengthGreaterThan5_CallsReplaceit()\n    {\n        // Arrange\n        var value = "123456";\n        var mock = new Mock<YourClass>();\n        mock.Setup(m => m.replaceit(value));\n\n        // Act\n        YourClass.modify(value);\n\n        // Assert\n        mock.Verify(m => m.replaceit(value), Times.Once);\n    }\n\n    [TestMethod]\n    public void Modify_ValueLengthLessThanOrEqualTo5_CallsChangeit()\n    {\n        // Arrange\n        var value = "12345";\n        var mock = new Mock<YourClass>();\n        mock.Setup(m => m.changeit(value));\n\n        // Act\n        YourClass.modify(value);\n\n        // Assert\n        mock.Verify(m => m.changeit(value), Times.Once);\n    }\n}\n\n// Shared test logic class\npublic static class TestHelpers\n{\n    public static void AssertReplaceitCalled(string value, Mock<YourClass> mock)\n    {\n        mock.Setup(m => m.replaceit(value));\n        YourClass.modify(value);\n        mock.Verify(m => m.replaceit(value), Times.Once);\n    }\n\n    public static void AssertChangeitCalled(string value, Mock<YourClass> mock)\n    {\n        mock.Setup(m => m.changeit(value));\n        YourClass.modify(value);\n        mock.Verify(m => m.changeit(value), Times.Once);\n    }\n}