List<Order> orders = ...; // your list of orders\n\nvar groupedOrders = orders.GroupBy(o => new { o.CustomerId, o.ProductId })\n                          .Select(g => new\n                          {\n                              CustomerId = g.Key.CustomerId,\n                              ProductId = g.Key.ProductId,\n                              TotalProductCount = g.Sum(o => o.ProductCount)\n                          });