using Microsoft.SharePoint.Client;\n\nnamespace SharePointPermissionExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            using (var context = new ClientContext("https://your-sharepoint-site.sharepoint.com"))\n            {\n                var list = context.Web.Lists.GetByTitle("Your List Title");\n                var userOrGroup = context.Web.EnsureUser("user@yourdomain.com"); // or group name\n                var roleDefinition = context.Web.RoleDefinitions.GetByName("Contribute");\n\n                var roleAssignment = new RoleAssignment(userOrGroup);\n                roleAssignment.RoleDefinitionBindings.Add(roleDefinition);\n\n                list.RoleAssignments.Add(roleAssignment);\n                context.ExecuteQuery();\n            }\n        }\n    }\n}