using System;\nusing System.Collections.Generic;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json;\n\nnamespace WordNetExample\n{\n    class Program\n    {\n        static async Task Main(string[] args)\n        {\n            string word = "intel";\n            var synonyms = await GetSynonyms(word);\n            foreach (var synonym in synonyms)\n            {\n                Console.WriteLine(synonym);\n            }\n        }\n\n        static async Task<List<string>> GetSynonyms(string word)\n        {\n            using (var client = new HttpClient())\n            {\n                var response = await client.GetAsync($"https://api.datamuse.com/words?rel_syn={word}");\n                response.EnsureSuccessStatusCode();\n                var responseBody = await response.Content.ReadAsStringAsync();\n                var data = JsonConvert.DeserializeObject<List<WordNetResponse>>(responseBody);\n                return data.Select(d => d.word).ToList();\n            }\n        }\n    }\n\n    public class WordNetResponse\n    {\n        public string word { get; set; }\n    }\n}