public bool CheckPRQTExists(string lastName, string cardNumber)\n{\n    // Create a dictionary to store the created PRQT records\n    Dictionary<string, bool> prqtRecords = new Dictionary<string, bool>();\n\n    // Check if the record already exists\n    if (prqtRecords.ContainsKey(lastName + cardNumber.Substring(cardNumber.Length - 4)))\n    {\n        return true; // Record already exists\n    }\n    else\n    {\n        prqtRecords.Add(lastName + cardNumber.Substring(cardNumber.Length - 4), true);\n        return false; // Record does not exist, create a new one\n    }\n}