public partial class User\n{\n    public string FirstName\n    {\n        get\n        {\n            var userData = UserData.FirstOrDefault(ud => ud.ItemID == this.UserID && ud.KeyName == "FirstName");\n            return userData != null ? userData.Value : string.Empty;\n        }\n        set\n        {\n            var userData = UserData.FirstOrDefault(ud => ud.ItemID == this.UserID && ud.KeyName == "FirstName");\n            if (userData == null)\n            {\n                userData = new UserData { ItemID = this.UserID, KeyName = "FirstName" };\n                UserData.Add(userData);\n            }\n            userData.Value = value;\n        }\n    }\n\n    // Similarly for LastName\n}