using System;\nusing System.Security.Principal;\nusing System.IO;\nusing System.Diagnostics;\n\nclass AuditEventParser\n{\n    static void Main(string[] args)\n    {\n        // Set up event log query\n        string logName = "Security";\n        string query = "EventID=4663"; // File system audit event\n\n        // Read and parse events\n        using (EventLogReader reader = new EventLogReader(logName, query))\n        {\n            foreach (EventRecord record in reader)\n            {\n                // Extract event data\n                string user = record.Properties["User"].Value.ToString();\n                string file = record.Properties["ObjectName"].Value.ToString();\n                string operation = record.Properties["OperationType"].Value.ToString();\n                DateTime time = record.TimeCreated;\n\n                // Process event data\n                Console.WriteLine($"User: {user}, File: {file}, Operation: {operation}, Time: {time}");\n                // Store data in file or database\n            }\n        }\n    }\n}