DataSet ds = ...; // your dataset\n\n// Get distinct insurance company names\nvar insuranceCompanies = ds.Tables[0].AsEnumerable()\n    .Select(r => r.Field<string>("insurance comp. name"))\n    .Distinct()\n    .ToList();\n\n// Sort data by name and date\nvar sortedData = ds.Tables[0].AsEnumerable()\n    .OrderBy(r => r.Field<string>("Name"))\n    .ThenBy(r => r.Field<DateTime>("treatmentDate"))\n    .CopyToDataTable();