using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class DatabaseHelper\n{\n    public static DataSet GetAllDataFromDatabase(string connectionString)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            DataSet dataSet = new DataSet();\n\n            // Get all table names\n            DataTable tableNames = connection.GetSchema("Tables");\n            foreach (DataRow tableName in tableNames.Rows)\n            {\n                string name = tableName["TABLE_NAME"].ToString();\n\n                // Skip system tables\n                if (name.StartsWith("sys"))\n                {\n                    continue;\n                }\n\n                using (SqlCommand command = new SqlCommand($"SELECT * FROM {name}", connection))\n                {\n                    using (SqlDataAdapter adapter = new SqlDataAdapter(command))\n                    {\n                        DataTable table = new DataTable(name);\n                        adapter.Fill(table);\n                        dataSet.Tables.Add(table);\n                    }\n                }\n            }\n\n            return dataSet;\n        }\n    }\n}