public static class LinkedListExtensions\n{\n    public static LinkedList<TOutput> ConvertAll<TInput, TOutput>(\n        this LinkedList<TInput> linkedList,\n        Converter<TInput, TOutput> converter)\n    {\n        var result = new LinkedList<TOutput>();\n\n        foreach (var item in linkedList)\n        {\n            result.AddLast(converter(item));\n        }\n\n        return result;\n    }\n}