using System.Data.OleDb;\n\npublic void AddToDatabase(string[] WordArray, int Good, int Bad, int Remove)\n{\n    string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=doom_calc_dict.mdb;jet OLEDB:database";\n    string query = "INSERT INTO WordDef (Word, Good, Bad, Remove) VALUES (@Word, @Good, @Bad, @Remove)";\n\n    using (OleDbConnection connection = new OleDbConnection(connectionString))\n    {\n        connection.Open();\n\n        for (int WordCount = 0; WordCount < WordArray.Length; WordCount++)\n        {\n            using (OleDbCommand command = new OleDbCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@Word", WordArray[WordCount]);\n                command.Parameters.AddWithValue("@Good", Good);\n                command.Parameters.AddWithValue("@Bad", Bad);\n                command.Parameters.AddWithValue("@Remove", Remove);\n\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}