using System.Globalization;\n\n// Get the current culture\nCultureInfo culture = Thread.CurrentThread.CurrentCulture;\n\n// Get the currency info\nCurrencyInfo currency = new CultureInfo(culture.Name).CurrencyInfo;\n\n// Format the currency\nstring formattedCurrency = $"{currency.DisplayName} {FormatCurrency(_nPrice)}";\n\n// Example output:\n// en-AU: Australian dollar 123.12\n// en-GB: British pound 123.12\n// en-US: United States dollar 123.12