using System.IO;\nusing iTextSharp.text.pdf;\n\n// Assume 'reportPdf' is the PDF generated by Reporting Services\n// and 'termsPdf' is the PDF version of the Terms & Conditions sheet\n\nusing (FileStream fs = new FileStream("output.pdf", FileMode.Create))\n{\n    PdfReader reportReader = new PdfReader(reportPdf);\n    PdfReader termsReader = new PdfReader(termsPdf);\n\n    PdfCopyFields copy = new PdfCopyFields(fs);\n    copy.AddDocument(reportReader);\n    copy.AddDocument(termsReader);\n    copy.Close();\n}