using Net.Pkcs11Interop.Common;\nusing Net.Pkcs11Interop.HighLevelAPI;\n\n// Load the PKCS #11 library provided by the HSM vendor\nPkcs11Library pkcs11Library = Pkcs11LibraryFactory.LoadPkcs11Library("path_to_pkcs11_library", AppType.MultiThreaded);\n\n// Initialize the PKCS #11 library\npkcs11Library.Initialize();\n\n// Get a slot (token) handle\nSlot slot = pkcs11Library.GetSlotList(SlotsType.WithTokenPresent)[0];\n\n// Open a session\nSession session = slot.OpenSession(SessionType.ReadOnly);\n\n// Perform cryptographic operations using the session\n// ...\n\n// Close the session\nsession.CloseSession();\n\n// Finalize the PKCS #11 library\npkcs11Library.Finalize();