SqlCeConnection conn = new SqlCeConnection("Data Source=your_database.sdf");\nconn.Open();\n\nSqlCeCommand cmd = new SqlCeCommand("SELECT SUBSTRING(image_data, @offset, @chunkSize) FROM your_table WHERE id = @id", conn);\n\nint offset = 0;\nint chunkSize = 4096; // adjust this value to control the chunk size\n\ncmd.Parameters.AddWithValue("@offset", offset);\ncmd.Parameters.AddWithValue("@chunkSize", chunkSize);\ncmd.Parameters.AddWithValue("@id", your_id_value);\n\nbyte[] chunk = new byte[chunkSize];\n\nwhile (true)\n{\n    SqlCeDataReader reader = cmd.ExecuteReader();\n    if (reader.Read())\n    {\n        int bytesRead = reader.GetBytes(0, 0, chunk, 0, chunkSize);\n        if (bytesRead == 0)\n        {\n            break; // end of data\n        }\n        // process the chunk\n        offset += bytesRead;\n        cmd.Parameters["@offset"].Value = offset;\n    }\n    else\n    {\n        break; // no more data\n    }\n}