public class MyService\n{\n    private readonly CiscoVpnClientChecker _vpnClientChecker;\n\n    public MyService()\n    {\n        _vpnClientChecker = new CiscoVpnClientChecker();\n    }\n\n    public void CheckVpnClientConnection()\n    {\n        if (_vpnClientChecker.IsVpnClientConnected())\n        {\n            Console.WriteLine("VPN client is connected");\n        }\n        else\n        {\n            Console.WriteLine("VPN client is not connected");\n        }\n    }\n}