public class ModelStateWarningDictionary : ModelStateDictionary\n{\n    public ModelStateWarningDictionary() : base()\n    {\n        Warnings = new ModelStateDictionary();\n    }\n\n    public ModelStateDictionary Warnings { get; private set; }\n\n    public void AddWarning(string key, string errorMessage)\n    {\n        Warnings.AddModelError(key, errorMessage);\n    }\n\n    public bool IsValidWithWarnings\n    {\n        get { return IsValid && Warnings.Count == 0; }\n    }\n}