public class CustomViewDataDictionary : ViewDataDictionary\n{\n    public CustomViewDataDictionary() : base()\n    {\n        ModelState = new ModelStateWarningDictionary();\n    }\n\n    public CustomViewDataDictionary(object model) : base(model)\n    {\n        ModelState = new ModelStateWarningDictionary();\n    }\n\n    public ModelStateWarningDictionary ModelStateWarning\n    {\n        get { return (ModelStateWarningDictionary)ModelState; }\n    }\n}