using System.Windows.Forms;\n\n// Create a new HtmlNode tree\nHtmlNode root = new HtmlNode(HtmlNodeType.Element, "div");\n\n// Create child nodes\nHtmlNode child1 = new HtmlNode(HtmlNodeType.Element, "p");\nHtmlNode child2 = new HtmlNode(HtmlNodeType.Element, "span");\n\n// Add child nodes to the root node\nroot.AppendChild(child1);\nroot.AppendChild(child2);\n\n// Create grandchild nodes\nHtmlNode grandchild1 = new HtmlNode(HtmlNodeType.Text, "Hello, world!");\nHtmlNode grandchild2 = new HtmlNode(HtmlNodeType.Element, "b");\n\n// Add grandchild nodes to the child nodes\nchild1.AppendChild(grandchild1);\nchild2.AppendChild(grandchild2);