using HtmlAgilityPack;\n\n// Create a new HtmlDocument\nHtmlDocument doc = new HtmlDocument();\n\n// Create a new HtmlNode tree\nHtmlNode root = doc.CreateElement("div");\n\n// Create child nodes\nHtmlNode child1 = doc.CreateElement("p");\nHtmlNode child2 = doc.CreateElement("span");\n\n// Add child nodes to the root node\nroot.AppendChild(child1);\nroot.AppendChild(child2);\n\n// Create grandchild nodes\nHtmlNode grandchild1 = doc.CreateTextNode("Hello, world!");\nHtmlNode grandchild2 = doc.CreateElement("b");\n\n// Add grandchild nodes to the child nodes\nchild1.AppendChild(grandchild1);\nchild2.AppendChild(grandchild2);\n\n// Save the HtmlNode tree to a string\nstring html = doc.DocumentNode.OuterHtml;