// Multiple Updates\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    for (int i = 0; i < 100; i++)\n    {\n        using (SqlCommand command = new SqlCommand("UPDATE table SET column = @value WHERE id = @id", connection))\n        {\n            command.Parameters.AddWithValue("@value", "new value");\n            command.Parameters.AddWithValue("@id", i);\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\n// DataSets\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM table", connection);\n    DataSet dataSet = new DataSet();\n    adapter.Fill(dataSet);\n\n    // Make changes to the DataSet\n    for (int i = 0; i < 100; i++)\n    {\n        dataSet.Tables[0].Rows[i]["column"] = "new value";\n    }\n\n    // Update the database\n    adapter.Update(dataSet);\n}