// Store the dynamic control's properties in ViewState\nViewState["DynamicImageButton"] = new Dictionary<string, string>\n{\n    { "ID", "uxStandardLetterDeleteImage_" + items.letterName },\n    { "CommandName", "uxStandardLetterDeleteImage_" + items.letterName },\n    { "ImageUrl", items.remove }\n};\n\n// Re-create the dynamic control on every postback\nprotected void Page_Init(object sender, EventArgs e)\n{\n    if (ViewState["DynamicImageButton"] != null)\n    {\n        Dictionary<string, string> imageButtonProperties = (Dictionary<string, string>)ViewState["DynamicImageButton"];\n        ImageButton imgbtnRemove = new ImageButton();\n        imgbtnRemove.ID = imageButtonProperties["ID"];\n        imgbtnRemove.CommandName = imageButtonProperties["CommandName"];\n        imgbtnRemove.ImageUrl = imageButtonProperties["ImageUrl"];\n        // Add the control to the table cell\n        tRow.Cells[3].Controls.Add(imgbtnRemove);\n    }\n}