using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Your SQL Server Connection String";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            DataTable schema = connection.GetSchema("Databases");\n            foreach (DataRow row in schema.Rows)\n            {\n                if (row["database_name"].ToString() == "YourDatabaseName")\n                {\n                    Console.WriteLine("Database Owner: " + row["owner"]);\n                    break;\n                }\n            }\n        }\n    }\n}