using Microsoft.Win32;\n\nclass ProxyHelper\n{\n    const string REGISTRY_KEY = @"HKEY_CURRENT_USER\Software\Microsoft\Windows\CurrentVersion\Internet Settings";\n\n    public void SetProxy(string proxyServer, int proxyPort)\n    {\n        Registry.SetValue(REGISTRY_KEY, "ProxyEnable", 1, RegistryValueKind.DWord);\n        Registry.SetValue(REGISTRY_KEY, "ProxyServer", proxyServer + ":" + proxyPort, RegistryValueKind.String);\n    }\n\n    public void DisableProxy()\n    {\n        Registry.SetValue(REGISTRY_KEY, "ProxyEnable", 0, RegistryValueKind.DWord);\n    }\n}