using System;\nusing System.Diagnostics;\nusing System.IO;\nusing PdfSharp.Pdf.Printing;\n\nnamespace PrintPdfFile\n{\n    class Program\n    {\n        [STAThread]\n        static void Main(string[] args)\n        {\n            // Set Acrobat Reader EXE, e.g.:\n            PdfFilePrinter.AdobeReaderPath = @"C:\Program Files\Adobe\Acrobat DC\Acrobat\Acrobat.exe";\n\n            // Set the file to print and the Windows name of the printer.\n            PdfFilePrinter printer = new PdfFilePrinter(@"C:\Path\To\Your\File.pdf", "YourPrinterName");\n\n            try\n            {\n                printer.Print();\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}