// Load the DLL and compute its hash\nbyte[] dllBytes = File.ReadAllBytes("MyDll.dll");\nusing (SHA256 sha256 = SHA256.Create())\n{\n    byte[] hashBytes = sha256.ComputeHash(dllBytes);\n    string hash = BitConverter.ToString(hashBytes).Replace("-", "").ToLower();\n    // Compare the hash with the stored hash\n    if (hash != storedHash)\n    {\n        throw new InvalidOperationException("DLL has been tampered with");\n    }\n}