using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Your SQL Server Connection String";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            SqlCommand command = new SqlCommand("SELECT owner_sid FROM sys.databases WHERE name = 'YourDatabaseName'", connection);\n            object ownerSid = command.ExecuteScalar();\n\n            // You may need to convert the owner_sid to a meaningful name\n            // depending on your requirements and how SQL Server is configured.\n            Console.WriteLine("Database Owner SID: " + ownerSid);\n        }\n    }\n}