private Dictionary<int, string> _processes = new Dictionary<int, string>();\n\nvoid SpawnNewProcess()\n{\n    string fileName = GetFileName();\n    System.Diagnostics.Process proc = new System.Diagnostics.Process();\n    proc.StartInfo.FileName = fileName;\n    proc.Start();\n    _processes.Add(proc.Id, fileName);\n    proc.Exited += new EventHandler(ProcessExited);\n    proc.EnableRaisingEvents = true;\n}\n\nprivate void ProcessExited(Object sender, EventArgs e)\n{\n    System.Diagnostics.Process proc = (System.Diagnostics.Process)sender;\n    string fileName = _processes[proc.Id];\n    Console.WriteLine($"Process {proc.Id} ({fileName}) has exited.");\n    _processes.Remove(proc.Id);\n}