public static double SquareRoot(double x) {\n    if (x == 0) return 0;\n\n    // Use a more informed initial estimate\n    double r = Math.Exp(Math.Log(x) / 2);\n    double last = 0;\n    double tolerance = 1e-12; // Convergence tolerance\n\n    while (true) {\n        r = (r + x / r) / 2;\n        if (Math.Abs(r - last) < tolerance)\n            break;\n        last = r;\n    }\n\n    return r;\n}