// Assuming you have a Users table with a UserId column\nstring uploadsFolder = @"C:\Uploads"; // outside of web root\nstring userFolder = Path.Combine(uploadsFolder, User.UserId.ToString());\nif (!Directory.Exists(userFolder))\n{\n    Directory.CreateDirectory(userFolder);\n}\n\n// Store the file in the user's folder\nstring fileName = Guid.NewGuid().ToString() + Path.GetExtension(fileUpload.FileName);\nstring filePath = Path.Combine(userFolder, fileName);\nfileUpload.SaveAs(filePath);\n\n// Store file metadata in the database\nvar fileMetadata = new FileMetadata\n{\n    UserId = User.UserId,\n    FilePath = filePath,\n    FileName = fileName,\n    // other metadata...\n};\ndb.FileMetadata.Add(fileMetadata);\ndb.SaveChanges();