// Base project (SchoolPda)\npublic interface IModule\n{\n    string ModuleName { get; }\n    void Initialize();\n}\n\npublic class MainMenu : Form\n{\n    private List<IModule> modules = new List<IModule>();\n\n    public MainMenu()\n    {\n        // Load modules dynamically\n        string[] dlls = Directory.GetFiles(".", "*.dll");\n        foreach (string dll in dlls)\n        {\n            Assembly asm = Assembly.LoadFrom(dll);\n            Type[] types = asm.GetTypes();\n            foreach (Type type in types)\n            {\n                if (typeof(IModule).IsAssignableFrom(type))\n                {\n                    IModule module = (IModule)Activator.CreateInstance(type);\n                    modules.Add(module);\n                }\n            }\n        }\n    }\n\n    private void Button_Click(object sender, EventArgs e)\n    {\n        // Show the module's UI\n        IModule module = modules.Find(m => m.ModuleName == (string)((Button)sender).Tag);\n        if (module != null)\n        {\n            module.Initialize();\n        }\n    }\n}\n\n// RegistrationModule project\npublic class RegistrationModule : IModule\n{\n    public string ModuleName { get { return "Registration"; } }\n\n    public void Initialize()\n    {\n        // Show the registration UI\n        RegistrationForm form = new RegistrationForm();\n        form.ShowDialog();\n    }\n}