using System;\nusing System.IO;\nusing Telerik.Web.UI;\n\npublic class CustomFileProvider : FileSystemContentProvider\n{\n    private string _rootPath;\n\n    public CustomFileProvider(string rootPath)\n    {\n        _rootPath = rootPath;\n    }\n\n    public override string GetPath(string virtualPath)\n    {\n        return Path.Combine(_rootPath, virtualPath);\n    }\n\n    public override DirectoryItem ResolveDirectory(string virtualPath)\n    {\n        string physicalPath = GetPath(virtualPath);\n        DirectoryInfo directoryInfo = new DirectoryInfo(physicalPath);\n\n        DirectoryItem directoryItem = new DirectoryItem(directoryInfo.Name, directoryInfo.FullName);\n\n        foreach (FileInfo fileInfo in directoryInfo.GetFiles())\n        {\n            directoryItem.Files.Add(new FileItem(fileInfo.Name, fileInfo.FullName));\n        }\n\n        foreach (DirectoryInfo subDirInfo in directoryInfo.GetDirectories())\n        {\n            directoryItem.Directories.Add(new DirectoryItem(subDirInfo.Name, subDirInfo.FullName));\n        }\n\n        return directoryItem;\n    }\n\n    public override FileItem ResolveFile(string virtualPath)\n    {\n        string physicalPath = GetPath(virtualPath);\n        FileInfo fileInfo = new FileInfo(physicalPath);\n\n        return new FileItem(fileInfo.Name, fileInfo.FullName);\n    }\n}