using Microsoft.Web.Administration;\n\npublic class CacheFlusher\n{\n    public static void FlushCache(string siteName, string applicationPath)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            Application application = site.Applications[applicationPath];\n\n            HttpRuntime runtime = application.GetRuntime();\n            runtime.Cache.RemoveAll();\n        }\n    }\n}