private static readonly ConcurrentDictionary<string, DataTable> MetadataCache = new ConcurrentDictionary<string, DataTable>();\n\npublic DataTable GetMetadata(string tableName)\n{\n    if (MetadataCache.TryGetValue(tableName, out DataTable metadata))\n    {\n        return metadata;\n    }\n\n    using (OracleCommand command = new OracleCommand("SELECT ...", connection))\n    {\n        command.Parameters.Add(new OracleParameter("TableName", tableName));\n        using (OracleDataAdapter adapter = new OracleDataAdapter(command))\n        {\n            DataTable metadataTable = new DataTable();\n            adapter.Fill(metadataTable);\n            MetadataCache.TryAdd(tableName, metadataTable);\n            return metadataTable;\n        }\n    }\n}