using System;\nusing System.Runtime.InteropServices;\n\nnamespace StorageCardSerialNumber\n{\n    public class Program\n    {\n        [DllImport("kernel32.dll")]\n        private static extern IntPtr GetStoreInformation(IntPtr hStore, out STORE_INFORMATION storeInfo);\n\n        [StructLayout(LayoutKind.Sequential)]\n        public struct STORE_INFORMATION\n        {\n            public int cbSize;\n            public int dwDeviceType;\n            public int dwFlags;\n            public int dwDeviceId;\n            [MarshalAs(UnmanagedType.LPWStr)]\n            public string szVolumeLabel;\n            [MarshalAs(UnmanagedType.LPWStr)]\n            public string szDeviceName;\n            [MarshalAs(UnmanagedType.LPWStr)]\n            public string szSerialNumber;\n        }\n\n        public static string GetStorageCardSerialNumber()\n        {\n            IntPtr hStore = IntPtr.Zero;\n            STORE_INFORMATION storeInfo = new STORE_INFORMATION();\n            storeInfo.cbSize = Marshal.SizeOf(typeof(STORE_INFORMATION));\n\n            hStore = GetStoreInformation(IntPtr.Zero, out storeInfo);\n\n            string serialNumber = storeInfo.szSerialNumber;\n\n            return serialNumber;\n        }\n\n        static void Main(string[] args)\n        {\n            string serialNumber = GetStorageCardSerialNumber();\n            Console.WriteLine(serialNumber);\n        }\n    }\n}