using ScrapySharp;\nusing ScrapySharp.Network;\n\n// Create a new instance of the ScrapySharp browser\nScrapySharpBrowser browser = new ScrapySharpBrowser();\n\n// Navigate to the webpage\nbrowser.NavigateToPage(new Uri("https://example.com"));\n\n// Wait for the page to finish loading\nbrowser.WaitForPageToLoad();\n\n// Get the HTML content\nstring html = browser.GetCurrentPage().Html;