public abstract class BaseController : Controller\n{\n    protected MasterViewModel model = new MasterViewModel();\n\n    public BaseController()\n    {\n        model.Buttons = new List<Button>();\n        //populate the button classes (doesn't matter how)\n        PopulateButtons(model.Buttons);\n    }\n\n    protected ViewResult ViewWithMasterModel(object viewModel)\n    {\n        MasterViewModel masterModel = new MasterViewModel();\n        masterModel.Buttons = model.Buttons;\n        // populate any other master page properties\n\n        // create a new instance of the view model that inherits from MasterViewModel\n        View1ViewModel view1ViewModel = new View1ViewModel();\n        view1ViewModel.SomeData = viewModel;\n        view1ViewModel.Buttons = masterModel.Buttons;\n\n        return View(view1ViewModel);\n    }\n}