using System.DirectoryServices.ActiveDirectory;\n\n// Replace with the domain user's username\nstring username = "domain\\username";\n\n// Create a new DirectoryEntry object for the domain\nDirectoryEntry domainEntry = new DirectoryEntry("LDAP://" + domainName);\n\n// Create a new DirectorySearcher object\nDirectorySearcher searcher = new DirectorySearcher(domainEntry);\n\n// Set the filter to search for computers with the user logged in\nsearcher.Filter = $"(&(objectClass=computer)(msDS-LastKnownGoodLogonTime<=9223372036854775807)(msDS-LastKnownGoodLogonUser={username}))";\n\n// Execute the search\nSearchResultCollection results = searcher.FindAll();\n\n// Iterate through the results and print the computer names\nforeach (SearchResult result in results)\n{\n    Console.WriteLine(result.Properties["cn"][0].ToString());\n}