protected override void Render(HtmlTextWriter writer)\n{\n    var html = new StringWriter();\n    base.Render(new HtmlTextWriter(html));\n\n    var htmlOutput = html.ToString();\n    var xmlDoc = new XmlDocument();\n    xmlDoc.LoadXml(htmlOutput);\n\n    var checkboxes = xmlDoc.SelectNodes("//input[@type='checkbox']");\n    foreach (XmlNode checkbox in checkboxes)\n    {\n        checkbox.Attributes["disabled"].Value = "disabled";\n        checkbox.Attributes["checked"].Value = "checked";\n    }\n\n    writer.Write(xmlDoc.OuterXml);\n}